// https://www.youtube.com/watch?v=hScR513gvNo&t=53s


import React, { Component } from 'react';

class Buscador extends Component {

    busquedaRef = React.createRef();
    // esto es para leer el input , tambien tenemos que decir donde se usara, lo usamos en el primer input ahi estamos creando un ref a ese input y podemos acceder alo que el usuario escriba
    // y lo correremos o leeremos cuan el usuario de click en submit(buscar) esto en js lo hariamos 
    // con document.queryselector al formulario y le pondriamos un adevenlistener pero react lo hace mas sencillo
    // en form agrega el evento onSubmit o cualquiera otro con un metodo o funcion que tenemos que crear antes de render

    obtenerDatos = (e) => {
        e.preventDefault();
        console.log(this.busquedaRef.current.value);
        // con esto leo el ref y lo comprobamos en consola  agregando algo al formulario y apretando buscar
        // para usar esto en nuestra aplicacion de react se lo tenemos que hacer saber al componente ppal (lo que escribio el usuario y comunicarlo con los componentes)
        // lo hacemos con props (esto pasa datos del componente ppal al hijo). ver en app.js componente <Buscador/>
        // para pasar texto insertado  en el hijo para pasarlo hacia el padre se hace con una funcion en el componente ppal (datosBusqueda)

        this.props.datosBusqueda(this.busquedaRef.current.value);


    }

    render() {
        return (
            <form onSubmit={this.obtenerDatos}>
                <div className='row'>
                    <div className='form-group col-md-8'>
                        <input ref={this.busquedaRef} type='text' className='form-control form-control-lg' placeholder='Busca tu imagen.Ejemplo: Futbol' />
                    </div>
                    <div className='form-group col-md-4'>
                        <input type='submit' className='btn btn-lg btn-danger btn-block' value='Buscar' />
                    </div>
                </div>
            </form>
        );
    }
}

export default Buscador;
