// https://www.youtube.com/watch?v=hScR513gvNo&t=53s



import React, { Component } from 'react'
import Buscador from "./componenetes/Buscador";
// minuto 30:48 resumen de lo que hemos hecho

class App extends Component {

  state = {
    termino: ''
  }

  consultarApi = () => {
    const url = `https://pixabay.com/api/videos/?key=27408680-8460fad8597c358ddb6d9c52a&q=${this.state.termino}`
    console.log(url)
  }

  datosBusqueda = (termino) => {
    this.setState({
      termino
    }, () => {
      this.consultarApi()
    })
  }



  render() {
    return (<div className="container">
      <div className="jumbotron">
        <p className="lead text-center">Buscador de Imagenes</p>

        <Buscador
          datosBusqueda={this.datosBusqueda}
        />
      </div>
      {this.state.termino}
    </div>
    );
  }
}
export default App;
