// https://www.youtube.com/watch?v=hScR513gvNo&t=53s



import React, { Component } from 'react'
import Buscador from "./componenetes/Buscador";
// minuto 30:48 resumen de lo que hemos hecho
import Resultado from './componenetes/Resulatdo';

class App extends Component {

  state = {
    termino: '',
    imagenes: []
  }

  consultarApi = () => {
    const termino = this.state.termino
    const url = `https://pixabay.com/api/?key=1732750-d45b5378879d1e877cd1d35a6&q=${termino}&per_page=30`
    // console.log(url)

    // para leer la api(datos Json) necesitamos fetch
    fetch(url)
      .then(respuesta => respuesta.json())
      .then(resultado => this.setState({ imagenes: resultado.hits }))
  }



  datosBusqueda = (termino) => {
    this.setState({
      termino
    }, () => {
      this.consultarApi()
    })
  }



  render() {
    return (<div className="container">
      <div className="jumbotron">
        <p className="lead text-center">Buscador de Imagenes</p>

        <Buscador
          datosBusqueda={this.datosBusqueda}
        />
      </div>
      <div>
        <Resultado
          imagenes={this.state.imagenes}
        />

      </div>


    </div>
    );
  }
}
export default App;
